mapper.select:
SELECT a.*,b.name as stock_name,b.code as stock_code FROM stock_trade a LEFT JOIN stock b ON a.stock_id = b.id
1 = 1
and b.name = #{name}
and a.time = #{trade_date}
ORDER BY a.id DESC

SELECT DISTINCT detail_date FROM stock_detail WHERE
	detail_date NOT IN (
		SELECT DISTINCT trade_date FROM stock_trade WHERE
			stock_id = #{_parameter}
	)
AND stock_id = #{_parameter}
AND detail_date >= (
	SELECT DISTINCT trade_date FROM stock_trade
	WHERE
		stock_id = #{_parameter}
	ORDER BY trade_date LIMIT 1
)
